import { NgModule } from '@angular/core';
import { RouterModule, Routes } from '@angular/router';
import { LoginComponent } from './login/login.component';
import { RegisterComponent } from './register/register.component';
import { VacancyTableComponent } from './vacancy-table/vacancy-table.component';
import { MainFrameComponent } from './main-frame/main-frame.component';

const routes: Routes = [
  {
    path: '',
    component: LoginComponent,
    children: [
      { path: 'login' , component: LoginComponent },      // Login dentro de MainFrameComponent
      { path: 'register', component: RegisterComponent } // Registro dentro de MainFrameComponent
    ]
  },
  { path: 'index', component: MainFrameComponent },   // Teste fora da hierarquia de MainFrameComponent
  { path: 'vacancies', component: VacancyTableComponent }, // Tabela de vagas
  { path: '**', redirectTo: 'login' } // Rota curinga para redirecionamento

];

@NgModule({
  imports: [RouterModule.forRoot(routes)],
  exports: [RouterModule]
})
export class AppRoutingModule { }
