import { Component } from '@angular/core';
import { VacanciesService } from '../service/vacancies/vacancies.service';

@Component({
  selector: 'app-vacancy-table',
  templateUrl: './vacancy-table.component.html',
  styleUrls: ['./vacancy-table.component.css']
})
export class VacancyTableComponent {

  list: any[] = [];
  isAdmin:boolean = false;

  status:string = '';
  title:string = '';
  description:string = '';
  requirements:string = '';
  id:string = '';

  constructor(private vacanciesService:VacanciesService){

  }

  ngOnInit() {
    this.isAdmin = 'admin' == localStorage.getItem('role')
    this.loadVacancies();
  }

  loadVacancies() {
    this.vacanciesService.getVacancies().subscribe({
      next: (data) => {
        this.list = data;
      },
      error: (err) => {
        console.error('Erro ao carregar vagas:', err);
      }
    });
  }

  setModal(status:any, title:any, description:any,requirements:any,  id:any){
    this.status = status;
    this.title = title;
    this.requirements = requirements;
    this.description = description;
    this.id = id;
  }

  deleteVacancy(vacancyId: number) {
    this.vacanciesService.deleteVacancy(vacancyId).subscribe({
      next: (data) => {
        console.log('Vaga deletada com sucesso');
        this.loadVacancies();  // Atualiza a lista ap처s a exclus찾o
      },
      error: (error) => {
        console.error('Erro ao deletar vaga:', error);
      }
    });
  }

}
